/**
 * 账号
 * 接口声明: {"name": "service.account"}
 */
export default class account {
    /**
     * 获取服务提供商
     * @returns 服务提供商的代号，如厂商的英文品牌名称，假如无此服务则返回空字符串
     */
    static getProvider(): string

    /**
     * 进行OAuth授权
     * @param obj
     * @property type 授权码模式为code，简化模式为token。
     * @property redirectUri 重定向URI
     * @property scope 申请的权限范围，目前只支持一种scope，假如不填则getProfile只返回openId。 scope.baseProfile：获取用户基本信息
     * @property state 可以指定任意值，认证服务器会原封不动地返回这个值
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static authorize(obj: {
        type: string,
        redirectUri?: string,
        scope?: string,
        state?: string,
        success?: Function,
        fail?: Function,
        complete?: Function
    }): void

    /**
     * 获得用户基本信息
     * @param obj
     * @property token 访问令牌
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static getProfile(obj: {
        token: string,
        success?: Function,
        fail?: Function,
        complete?: Function
    }): void
}
