/**
 * 支付
 * 接口声明: {"name": "service.pay"}
 */
export default class pay {
    /**
     * 获取服务提供商
     * @returns 服务提供商的代号，如厂商的英文品牌名称，假如无此服务则返回空字符串
     */
    static getProvider(): string

    /**
     * 使用支付完成付款
     * @param obj
     * @property orderInfo 订单信息
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static pay(obj: { orderInfo: string, success?: Function, fail?: Function, complete?: Function }): void
}
