/**
 * 推送
 * 接口声明: {"name": "service.push"}
 */
export default class push {
    /**
     * 获取服务提供商
     * @returns 服务提供商的代号，如厂商的英文品牌名称，假如无此服务则返回空字符串
     */
    static getProvider(): string

    /**
     * 订阅push，后续可以收到push消息（一般可在应用初始化的地方进行调用。比如在app的onCreate方法中调用。）
     * @param obj
     * @property success 成功回调
     * @property fail 失败回调，返回失败原因
     * @property complete 执行结束后的回调
     */
    static subscribe(obj: { success?: Function, fail?: Function, complete?: Function }): void

    /**
     * 取消订阅（一般不建议调用，调用后regId失效，需要重新订阅获取新的regId）
     */
    static unsubscribe(): void

    /**
     * 添加push事件回调（透传消息的payload内容可在此回调中收到）
     * @param obj
     * @property callback push事件回调处理
     */
    static on(obj: { callback?: Function }): void

    /**
     * 移除push事件回调，push.on中的callback不会再收到透传内容
     * @param obj
     * @property callback push事件回调处理
     */
    static off(): void
}
