/**
 * 第三方分享
 * 接口声明: {"name": "service.share"}
 */
export default class share {
    /**
     * 获取服务提供商
     * @returns 服务提供商的代号，如厂商的英文品牌名称，假如无此服务则返回空字符串
     */
    static getProvider(): string

    /**
     * 分享内容
     * @param obj
     * @property shareType 分享类型。0：默认图文，1：纯文字，2：纯图片，3：音乐，4：视频。
     * @property title 分享的标题, 分享类型0,1,3,4必填
     * @property summary 分享的摘要
     * @property targetUrl 点击后的跳转URL, 分享类型0,3,4必填
     * @property imagePath 分享图片/缩略图的本地地址; 另外可支持分享在线图片的url
     * @property mediaUrl 分享的音乐/视频数据URL, 分享类型3,4必填
     * @property platforms 分享到的平台，不填则默认分享所有平台。可用值包括：WEIBO(新浪微博)、QQ、WEIXIN(微信好友)、WEIXIN_CIRCLE(微信朋友圈)、SYSTEM(系统分享)
     * @property success 成功回调(暂不支持)
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static share(obj: {
        shareType: number,
        title?: string,
        summary?: string,
        targetUrl?: string,
        imagePath?: string,
        mediaUrl?: string,
        platforms?: string[],
        success?: Function,
        fail?: Function,
        complete?: Function
    }): void

    /**
     * 获取当前可用的支持分享的平台列表
     * @param obj
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static getAvailablePlatforms(obj: {
        success?: Function,
        fail?: Function,
        complete?: Function
    }): void
}
