/**
 * 微信账户
 * 接口声明: {"name": "service.wxaccount"}
 */
export default class wxaccount {
    /**
     * 获取当前的微信登陆方式
     * @returns APP: 微信app登陆方式; none: 当前微信登录不可用，微信未安装或者手机系统不支持;
     */
    static getType(): any

    /**
     * 发起微信登陆，调用之前应该先使用getType函数查询APP登陆方式是否被支持
     * @param obj
     * @property scope 应用授权作用域，如获取用户个人信息则填写snsapi_userinfo
     * @property state 用于保持请求和回调的状态，授权请求后原样带回给第三方。该参数可用于防止csrf攻击（跨站请求伪造攻击），建议第三方带上该参数，可设置为简单的随机数加session进行校验
     * @property success 成功回调
     * @property fail 失败回调
     * @property cancel 取消回调
     */
    static authorize(obj: {
        scope: string,
        state?: string,
        success?: Function,
        fail?: Function,
        cancel?: Function
    }): void
}
