// Definitions by: Hap <https://www.quickapp.cn/>
/**
 * 音频
 * 接口声明: {"name": "system.audio"}
 */
export default class audio {
  /**
   * 播放的音频媒体uri
   */
  static src: string;

  /**
   * 音频的当前进度，单位秒，对值设置可以调整播放进度
   */
  static currentTime: number;

  /**
   * 音频的播放时长，单位秒，未知返回NaN
   */
  static duration: number;

  /**
   * 音频是否自动播放，默认false
   */
  static autoplay: boolean;

  /**
   * 音频是否循环播放，默认false
   */
  static loop: boolean;

  /**
   * 音频的音量，默认当前系统媒体音量，音量变化范围[0.0,1.0]
   */
  static volume: number;

  /**
   * 音频是否静音，默认false
   */
  static muted: boolean;

  /**
  * 音频标题，未设置时显示正在播放或者暂停播放
  */
  static title: string;

  /**
  *  歌手名，未设置时显示正在播放或者暂停播放
  */
  static artist: string;

  /**
   *  指定使用音频类型，可设置的值有 music、voicecall，值为 music 时使用扬声器播放，voicecall 时使用听筒播放，默认为 music。
   */
  static cover: string;

  /**
   * 指定使用音频类型，可设置的值有 music、voicecall，值为 music 时使用扬声器播放，voicecall 时使用听筒播放，默认为 music
   */
  static streamType: string;

  /**
   * 音频是否在通知栏中显示音频播放状态，默认true
   */
  static notificationVisible: boolean;

  /**
   * 在调用play方法后或者autoplay为true时的回调事件
   */
  static onplay: () => void;

  /**
   * 在调用pause方法后的回调事件
   */
  static onpause: () => void;

  /**
   * 在调用 stop 方法后的回调事件
   */
  static onstop: () => void;

  /**
   * 第一次获取到音频数据的回调事件
   */
  static onloadeddata: () => void;

  /**
   * 播放结束时的回调事件
   */
  static onended: () => void;

  /**
   * 播放时长变化时的回调事件
   */
  static ondurationchange: () => void;

  /**
   * 播放发生错误时的回调事件
   */
  static onerror: () => void;

  /**
   * 播放进度变化时触发，触发频率4HZ
   */
  static ontimeupdate: () => void;

  /**
   * 通知栏点击上一首按钮时触发
   */
  static onprevious: () => void;

  /**
   * 通知栏点击下一首按钮时触发
   */
  static onnext: () => {};
  /**
   * 开始播放音频
   */
  static play(): void

  /**
   * 暂停播放音频
   */
  static pause(): void

  /**
   * 停止音频播放，可以通过 play 重新播放音频
   */
  static stop(): void
}
