/**
 * 日历事件
 * 接口声明: {"name": "system.calendar"}
 */
export default class calendar {
    /**
     * 插入日历事件
     * @param obj
     * @property title 事件的标题
     * @property description 事件的描述
     * @property startDate 事件开始时间，以从公元纪年开始计算的协调世界时毫秒数表示
     * @property endDate 事件结束时间，以从公元纪年开始计算的协调世界时毫秒数表示
     * @property timezone 事件的时区
     * @property allDay true 表示此事件占用一整天（按照本地时区的定义）。 false 表示它是常规事件，可在一天内的任何时间开始和结束
     * @property rule 事件的重复发生规则格式。例如，"FREQ=WEEKLY;COUNT=10;WKST=SU"。 您可以在此处找到更多示例
     * @property remindMinutes 在事件开始前几分钟进行提醒。例如：[5,15,30]
     * @property organizer 事件组织者（所有者）的电子邮件
     * @property success 成功回调，值为插入成功的id
     * @property fail 失败回调
     * @property cancel 取消回调
     */
    static insert(obj: {
        title: string,
        description?: string,
        startDate: number,
        endDate: number,
        timezone?: string,
        allDay?: boolean,
        rule?: string,
        remindMinutes?: number[],
        organizer?: string,
        success?: Function,
        fail?: Function,
        cancel?: Function,
    }): void
}
