/**
 * 剪贴板
 * 接口声明: {"name": "system.clipboard"}
 */
export default class clipboard {
    /**
     * 修改剪贴板内容
     * @param obj
     * @property text 需要放到剪切板的内容
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static set(obj: { text: string, success?: Function, fail?: Function, complete?: Function }): void

    /**
     * 读取剪贴板内容
     * @param obj 
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static get(obj: { success?: Function, fail?: Function, complete?: Function }): void

}
