/**
 * 设备信息
 * 接口声明: {"name": "system.device"}
 */
export default class device {
    /**
     * 平台版本信息
     */
    platform: platform;

    /**
     * 获取设备信息
     * @param obj
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static getInfo(obj: { success?: Function, fail?: Function, complete?: Function }): void

    /**
     * 获取设备信息
     * @param obj
     * @property type 支持device、mac、user、advertising 1000+四种类型，可提供一至多个
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static getId(obj: { type: string[], success?: Function, fail?: Function, complete?: Function }): void

    /**
     * 获取设备唯一标识。需要用户授权
     * @param obj
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static getDeviceId(obj: { success?: Function, fail?: Function, complete?: Function }): void

    /**
     * 获取广告唯一标识
     * @param obj
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static getAdvertisingId(obj: { success?: Function, fail?: Function, complete?: Function }): void

    /**
     * 获取存储空间的总大小
     * @param obj
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static getTotalStorage(obj: { success?: Function, fail?: Function, complete?: Function }): void

    /**
     * 获取存储空间的可用大小
     * @param obj
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static getAvailableStorage(obj: { success?: Function, fail?: Function, complete?: Function }): void

    /**
     * 获取存储空间的可用大小
     * @param obj
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static getCpuInfo(obj: { success?: Function, fail?: Function, complete?: Function }): void

     /**
     * 获取设备序列号
     * @param obj
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static getSerial(obj:{success?: Function, fail?: Function, complete?: Function}): void

}

/**
 * 平台版本信息
 */
export class platform {
    /**
     * 运行平台版本名称
     */
    versionName: string;

    /**
     * 运行平台版本号
     */
    versionCode: string;
}