/**
 * 数据请求
 * 接口声明: {"name": "system.fetch"}
 */
export default class fetch {
    /**
     * 获取网络数据
     * @param obj
     * @property url 资源url
     * @property data 请求的参数，可以是字符串，或者是 js 对象、arraybuffer 对象。参考 data与Content-Type关系 部分
     * @property header 请求的header，会将其所有属性设置到请求的header部分。useragent在1040支持。示例：{"Accept-Encoding": "gzip, deflate","Accept-Language": "zh-CN,en-US;q=0.8,en;q=0.6"}
     * @property method 默认为 GET，可以是：OPTIONS, GET, HEAD, POST, PUT, DELETE, TRACE, CONNECT
     * @property responseType 支持返回类型是 text，json，file，arraybuffer，默认会根据服务器返回 header 中的 Content-Type 确定返回类型，详见 success返回值。
     * @property success 成功返回的回调函数
     * @property fail 失败的回调函数
     * @property complete 结束的回调函数（调用成功、失败都会执行）
     */
    static fetch(obj: {
        url: string,
        data?: string | object | ArrayBuffer,
        header?: object,
        method?: string,
        responseType?: string,
        success?: Function,
        fail?: Function,
        complete?: Function
    }): void
}
