/**
 * 多媒体
 * 接口声明: {"name": "system.media"}
 */
export default class media {
    /**
     * 拍摄照片
     * @param obj
     * @property success 成功回调
     * @property fail 失败回调
     * @property cancel 取消回调
     * @property complete 执行结束后的回调
     */
    static takePhoto(obj: { success?: Function, fail?: Function, cancel?: Function, complete?: Function }): void

    /**
     * 拍摄视频
     * @param obj
     * @property success 成功回调
     * @property fail 失败回调
     * @property cancel 取消回调
     * @property complete 执行结束后的回调
     */
    static takeVideo(obj: { success?: Function, fail?: Function, cancel?: Function, complete?: Function }): void
    /**
     * 选择图片
     * @param obj
     * @property success 成功回调
     * @property fail 失败回调
     * @property cancel 取消回调
     * @property complete 执行结束后的回调
     */
    static pickImage(obj: { success?: Function, fail?: Function, cancel?: Function, complete?: Function }): void

    /**
     * 选择视频
     * @param obj
     * @property success 成功回调
     * @property fail 失败回调
     * @property cancel 取消回调
     * @property complete 执行结束后的回调
     */
    static pickVideo(obj: { success?: Function, fail?: Function, cancel?: Function, complete?: Function }): void

    /**
     * 选择文件
     * @param obj
     * @property success 成功回调
     * @property fail 失败回调
     * @property cancel 取消回调
     * @property complete 执行结束后的回调
     */
    static pickFile(obj: { success?: Function, fail?: Function, cancel?: Function, complete?: Function }): void

    /**
     * 将图片/视频保存到相册中．需要用户授权
     * @param obj
     * @property uri 源文件的uri，文件的扩展名必须是图片或视频的扩展名
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static saveToPhotosAlbum(obj: { uri: string, success?: Function, fail?: Function, complete?: Function }): void

    /**
     * 获取系统铃声。如果是获取来电铃声，双卡情况下，获取的是卡1对应的铃声。
     * @param obj
     * @property type 铃声类型，ringtone：来电，notification：通知，alarm：闹钟
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static getRingtone(obj: { type: string, success?: Function, fail?: Function, complete?: Function }): void;

    /**
     * 设置系统铃声，目前只支持本地文件。如果是设置来电铃声，双卡情况下，卡1卡2对应的铃声都会设置。
     * @param obj
     * @property uri 铃声文件路径，只支持本地文件
     * @property type 铃声类型，ringtone：来电，notification：通知，alarm：闹钟
     * @property title 铃声名称，没有设置默认取文件名
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static setRingtone(obj: { uri: string, type: string, title?: string, sccuess?: Function, fail?: Function, complete?: Function }): void;

    /**
     * 设置系统铃声，目前只支持本地文件。如果是设置来电铃声，双卡情况下，卡1卡2对应的铃声都会设置。
     * @param obj
     * @property current 数据类型可选择Number或者String：
     * Number：当前显示的图片的下标，默认0；
     * String：当前显示的图片链接，默认为uris中的第一张的地址
     * @property uris 需要预览的图片链接列表，同时支持网络和本地地址
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static previewImage(obj: { current?: number | string, uris: Array<string>, sccuess?: Function, fail?: Function, complete?: Function }): void;

    /**
     * 选择多张图片
     * @param obj
     * @property cancel 成功回调
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static pickImages(obj: { sccuess?: Function, cancel?: Function, fail?: Function, complete?: Function }): void;

    /**
     * 选择多个视频
     * @param obj
     * @property cancel 成功回调
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static pickVideos(obj: { sccuess: Function, cancel: Function, fail: Function, complete: Function }): void;
}
