/**
 * 网络状态
 * 接口声明: {"name": "system.network"}
 */
export default class network {
    /**
     * 获取网络类型
     * @param obj
     * @property success 成功回调
     * @property fail 失败回调，可能是因为缺乏权限
     * @property complete 执行结束后的回调
     */
    static getType(obj: { success?: Function, fail?: Function, complete?: Function }): void

    /**
     * 监听网络连接状态。如果多次调用，仅最后一次调用生效
     * @param obj
     * @property callback 每次网络发生变化，都会被回调
     * @property fail 失败回调，可能是因为缺乏权限
     */
    static subscribe(obj: { callback?: Function, fail?: Function }): void


    /**
     * 取消监听网络连接状态
     */
    static unsubscribe(): void
}
