/**
 * 弹窗
 * 接口声明: {"name": "system.prompt"}
 */
export default class prompt {
    /**
     * 显示Toast
     * @param obj
     * @property message 要显示的文本
     * @property duration 0为短时，1为长时，默认0
     */
    static showToast(obj: { message: string, duration?: number }): void

    /**
     * 显示对话框
     * @param obj 
     * @property title 标题
     * @property message 内容
     * @property buttons 按钮的数组，按钮结构：{text:'text',color:'#333333'}，color可选：buttons的第1项为positive button；buttons的第2项（如果有）为negative button；buttons的第3项（如果有）为neutral button。最多支持3个button
     * @property success 成功回调
     * @property cancel 取消回调
     * @property complete 执行结束后的回调
     */
    static showDialog(obj: {
        title?: string, message?: string,
        buttons?: [{ text: string, color?: string }],
        success?: Function,
        cancel?: Function,
        fail?: Function,
        complete?: Function
    }): void

    /**
     * 显示上下文菜单
     * @param obj 
     * @property itemList 按钮的文字数组
     * @property itemColor 按钮颜色
     * @property success 成功回调
     * @property cancel 取消回调
     * @property complete 执行结束后的回调
     */
    static showContextMenu(obj: {
        itemList: string[],
        itemColor?: string,
        success?: Function,
        cancel?: Function,
        complete?: Function
    }): void
}
