/**
 * 录音
 * 接口声明: {"name": "system.record"}
 */
export default class record {
    /**
     * 检测应用是否存在。支持检测原生应用是否已安装
     * @param obj
     * @property duration 录音时长，单位为ms。如果duration为有效值将在达到指定值时停止录音
     * @property sampleRate 采样率。不同的音频格式所支持的采样率范围不同。对于aac格式，默认设置为8000，建议使用 8000/16000/44100
     * @property numberOfChannels 录音通道数，有效值 1/2
     * @property encodeBitRate 编码码率。编码码率的取值与采样率和音频格式有关。对aac格式，建议按照下表中取值范围来选择编码码率
     * @property format 音频格式，有效值 3gpp/amr_nb/aac。缺省为3gpp
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static start(obj: {
        duration?: number,
        sampleRate?: number,
        numberOfChannels?: number,
        encodeBitRate?: number,
        format?: string,
        success?: Function,
        fail?: Function,
        complete?: Function
    }): void

    /**
     * 停止录音
     */
    static stop(): void
}
