/**
 * 上传下载
 * 接口声明: {"name": "system.request"}
 */
export default class request {
    /**
     * 上传文件
     * @param obj
     * @property url 资源url
     * @property header 请求的header，会将其所有属性设置到请求的header部分。useragent设置无效。
     * @property method 默认为POST，可以是： POST, PUT
     * @property files 需要上传的文件列表，使用multipart/form-data方式提交
     * @property data HTTP请求中其他额外的form data
     * @property success 成功返回的回调函数
     * @property fail 失败的回调函数
     * @property complete 结束的回调函数（调用成功、失败都会执行）
     */
    static upload(obj: {
        url: string,
        header?: object,
        method?: string,
        files: [{ filename?: string, name?: string, uri: string, type?: string }],
        data?: [{ name: string, value: string }],
        success?: Function,
        fail?: Function,
        complete?: Function
    }): void

    /**
     * 下载文件
     * @param obj 
     * @property url 资源url
     * @property header 请求的header，会将其所有属性设置到请求的header部分。useragent设置无效。
     * @property description 下载描述，会用于通知栏标题。默认为文件名
     * @property filename 下载文件名。默认从网络请求或url中获取
     * @property success 成功返回的回调函数
     * @property fail 失败的回调函数
     * @property complete 结束的回调函数（调用成功、失败都会执行）
     */
    static download(obj: {
        url: string,
        header?: string,
        description?: string,
        filename?: string,
        success?: Function,
        fail?: Function,
        complete?: Function
    }): void

    /**
     * 监听下载任务
     * @param obj 
     * @property token download接口返回的token
     * @property success 成功返回的回调函数
     * @property fail 失败的回调函数
     * @property complete 结束的回调函数（调用成功、失败都会执行）
     */
    static onDownloadComplete(obj: {
        token: string,
        success?: Function,
        fail?: Function,
        complete?: Function
    }): void
}
