/**
 * 传感器
 * 接口声明: {"name": "system.sensor"}
 */
export default class sensor {
    /**
     * 监听重力感应数据。如果多次调用，仅最后一次调用生效
     * @param obj
     * @property callback 重力感应数据变化后会回调此函数，5次每秒
     */
    static subscribeAccelerometer(obj: { callback?: Function }): void

    /**
     * 取消监听重力感应数据
     */
    static unsubscribeAccelerometer(): void

    /**
     * 监听罗盘数据。如果多次调用，仅最后一次调用生效
     * @param obj 
     * @property callback 重力感应数据变化后会回调此函数，5次每秒
     */
    static subscribeAccelerometer(obj: { callback?: Function }): void

    /**
     * 取消监听罗盘数据
     */
    static unsubscribeCompass(): void

    /**
     * 监听距离感应数据。如果多次调用，仅最后一次调用生效。
     * @param obj 
     * @property callback 距离感应数据变化后会回调此函数
     */
    static subscribeProximity(obj: { callback?: Function }): void

    /**
     * 取消监听距离感应数据。
     */
    static unsubscribeProximity(): void

    /**
     * 监听光线感应数据。如果多次调用，仅最后一次调用生效
     * @param obj 
     * @property callback 光线感应数据变化后会回调此函数
     */
    static subscribeLight(obj: { callback?: Function }): void

    /**
     * 取消监听光线感应数据
     */
    static unsubscribeLight(): void
}
