/**
 * 桌面图标
 * 接口声明: {"name": "system.shortcut"}
 */
export default class shortcut {
    /**
     * 获取桌面图标是否创建
     * @param obj
     * @property success 成功回调。参数：true 已创建，false 未创建
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static hasInstalled(obj: { success?: Function, fail?: Function, complete?: Function }): void

    /**
     * 创建桌面图标，需要用户允许
     * @param obj
     * @property message 权限弹窗上的说明文字，用于向用户解释为什么要创建桌面图标
     * @property success 创建成功
     * @property fail 创建失败
     * @property complete 执行结束后的回调
     */
    static install(obj: { message?: string, success?: Function, fail?: Function, complete?: Function }): void

    /**
     * 是否开启系统快捷方式创建弹窗，默认true。不会持久化，只对当前运行有效
     */
    systemPromptEnabled: boolean;
}
