/**
 * 数据存储
 * 接口声明: {"name": "system.storage"}
 */
export default class storage {
    /**
     * 读取存储内容
     * @param obj
     * @property key 索引
     * @property default 如果key不存在，返回default。如果default未指定，返回长度为0的空字符串
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static get(obj: { key: string, default?: string, success?: Function, fail?: Function, complete?: Function }): void

    /**
     * 修改存储内容
     * @param obj
     * @property key 索引
     * @property value 新值。如果新值是长度为0的空字符串，会删除以key为索引的数据项
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static set(obj: { key: string, value?: string, success?: Function, fail?: Function, complete?: Function }): void

    /**
     * 清空存储内容
     * @param obj 
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static clear(obj: { success?: Function, fail?: Function, complete?: Function }): void

    /**
     * 删除存储内容
     * @param obj 
     * @property key 索引
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static delete(obj: { key: string, success?: Function, fail?: Function, complete?: Function }): void
}
