/**
 * 系统音量
 * 接口声明: {"name": "system.volume"}
 */
export default class volume {
    /**
     * 获取当前多媒体音量
     * @param obj
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static getMediaValue(obj: { success?: Function, fail?: Function, complete?: Function }): void

    /**
     * 获取当前多媒体音量
     * @param obj
     * @property value 设置的音量，0.0-1.0之间
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static setMediaValue(obj: { value: number, success?: Function, fail?: Function, complete?: Function }): void
}
