/**
 * Wi-Fi
 * 接口声明: {"name": "system.wifi"}
 */
export default class wifi {
    /**
     * 连接Wi-Fi。若已知Wi-Fi信息，可以直接利用该接口连接
     * @param obj
     * @property SSID Wi-Fi设备的SSID
     * @property BSSID Wi-Fi设备的BSSID
     * @property password Wi-Fi设备密码
     * @property success 成功回调
     * @property fail 失败回调
     * @property complete 执行结束后的回调
     */
    static connect(obj: { SSID: string, BSSID: string, password?: string, success?: Function, fail?: Function, complete?: Function }): void

    /**
     * 请求获取Wi-Fi列表，在onscanned事件中返回Wi-Fi列表数据
     * @param obj
     * @property success 扫描请求发起成功
     * @property fail 扫描请求发起失败
     * @property complete 执行结束后的回调
     */
    static scan(obj: { success?: Function, fail?: Function, complete?: Function }): void

    /**
     * 获取已连接中的Wi-Fi信息
     * @param obj
     * @property success 扫描请求发起成功
     * @property fail 扫描请求发起失败
     * @property complete 执行结束后的回调
     */
    static getConnectedWifi(obj: { success?: Function, fail?: Function, complete?: Function }): void

    /**
     * 监听在获取到Wi-Fi列表数据时的事件，在回调中将返回wifiList
     */
    onscanned: (data: { wifiList: { SSID: string, BSSID: String, secure: boolean, signalStrength: number } }) => void;

    /**
     * 监听连接和断开Wi-Fi的事件
     */
    onstatechanged: (data: { state: number, SSID: string, BSSID: String, secure: boolean, signalStrength: number }) => void;
}
